require 'set'

input = ''

def unique_encoding(length, input)
  chars = []
  char_count = 0

  input.each_char do |char|
    char_count += 1
    chars << char
    break if chars.length == length && (Set.new(chars).length == length)

    chars.shift if chars.length == length
  end

  char_count
end

# Part 1
puts unique_encoding(4, input)

# Part 2
puts unique_encoding(14, input)
